(window.webpackJsonp=window.webpackJsonp||[]).push([[118],{567:function(n,t,a){"use strict";a.r(t),t.default="/* globals spritevue */\nconst Vue = spritevue.Vue;\nfunction randomColor() {\n  const r = Math.random() * 255 | 0,\n    g = Math.random() * 192 | 0,\n    b = Math.random() * 128 | 0;\n  return `rgb(${r},${g},${b})`;\n}\n\nVue.component('my-circle', {\n  data() {\n    return {\n      state: 'stateA',\n      states: {\n        stateA: {\n          bgcolor: randomColor,\n        },\n        stateB: {\n          bgcolor: randomColor,\n        },\n      },\n      actions: [\n        {\n          both: ['stateA', 'stateB'],\n          duration: 600,\n          easing: 'ease-in-out',\n        },\n      ],\n    };\n  },\n  props: [\n    'radius',\n    'x',\n    'y',\n  ],\n  methods: {\n    click() {\n      const state = this.state;\n      this.state = state === 'stateA' ? 'stateB' : 'stateA';\n    },\n  },\n  template: `<sprite ref=\"circle\" anchor=\"0.5\" :x=\"x\" :y=\"y\" :size=\"[2*radius, 2*radius]\"\n:states=\"states\" :actions=\"actions\" :state=\"state\" :borderRadius=\"radius\" @click=\"click\"></sprite>`,\n});\n\nnew Vue({\n  el: '#app',\n  data() {\n    return {\n      font: '48px Arial',\n      fillColor: '#f50',\n    };\n  },\n});"}}]);